mapper.select:
SELECT , from eosorg_t_organization where orgID IN
#{item}
order by FIELD( orgId , #{item} )

SELECT 
FROM eosorg_t_organization org
WHERE  1=1

    and org.orgCode=#{orgCode}


    and org.orgseq like #{orgseqLike}


    and org.orgName like #{orgName}

SELECT , from eosorg_t_organization where orgID IN
#{item}
and status=1 and tenantId is not null order by length(orgseq) desc

select 
from eosorg_t_organization org LEFT JOIN ordertenant ot  ON  org.orgId=ot.orgId WHERE 1=1

  and org.parentOrgId=#{parentOrgId}


  and org.status=#{status}

ORDER BY IF(ISNULL(ot.order),0,1) DESC, ot.order ASC, org.status DESC, org.sortId, CONVERT(org.orgName USING 'gbk')

select
,
 from eosorg_t_organization where 1=1 and orgName = #{orgName}

  and parentOrgId = #{parentOrgId}


  and status = #{status}


  and status = true

order by status desc,sortId, CONVERT(orgName USING 'gbk')

SELECT model.orgID FROM eosorg_t_organization AS model where 1 = 1

  and model.orgSeq LIKE '1.%' OR model.orgId = 1 OR model.parentOrgID = 1


  and model.orgSeq LIKE CONCAT('%',#{_parameter},'%') OR model.orgID = #{_parameter} OR model.parentOrgID = #{_parameter}
