(function($) {
	$.fn.hhShare = function(t) {
		var n = {
			cenBox: "asid_share_box",
			icon: "adid_icon",
			addClass: "red_bag",
			titleClass: "asid_title",
			triangle: "asid_share_triangle",
			showBox: "asid_sha_layer"
		},
		r = $.extend(n, t);
		var $scrollUp=$("." + r.cenBox).last();
		$scrollUp.hide();
		$("." + r.triangle + "," + "." + r.showBox).hide();
		if($(window).scrollTop() > 300 ){
			$scrollUp.fadeIn();
		}
		$(window).scroll(function() {
			$(window).scrollTop() > 300 ?$scrollUp.fadeIn() : $scrollUp.fadeOut()
		});
		$scrollUp.click(function() {
			return $("body,html").animate({
				scrollTop: 0
			},
			500),
			!1
		});
		$("."+r.cenBox).hover(function(){
			var t = $(this).find("." + r.icon),
			title = $(this).find("." + r.icon).attr("alt");
			t.hide();
			$(this).addClass(r.addClass);
			$(this).children("a").append('<b class="' + r.titleClass + '">' + title + "</b>");
			$(this).find("." + r.triangle + "," + "." + r.showBox).show();	
		},function(){
			var t = $(this).find("." + r.icon);
			t.show(),
			$(this).removeClass(r.addClass),
			$(this).find("." + r.titleClass).remove(),
			$(this).find("." + r.triangle + "," + "." + r.showBox).hide();
		});
	}
})(jQuery);

